#include "PhoneBook.Class.hpp"std::string truncate(std::string str, size_t width){	if (str.length() > width)		return str.substr(0, width - 1) + ".";	return str;}void	print_str(std::string str) {	std::cout << "|" ;	std::cout << std::left << std::setw(10) << truncate(str, 10);}void	print_int(std::int32_t integer) {	std::cout << "|" ;	std::cout << std::left << std::setw(10) << integer;}int	main(void) {	PhoneBook	pb;	std::string input;	std::cout << std::endl << "The program only accepts ADD, SEARCH and EXIT." << std::endl;	while (true) {		std::cout << "Enter one of those (ADD, SEARCH, EXIT): ";		std::cin >> input;		if (std::cin.eof())			exit(0);		if (input == "ADD")			pb.add();		else if (input == "SEARCH")			pb.show();		else if (input == "EXIT")			exit(0);	}	return (0);}