#include "PhoneBook.hpp"int	main(void) {	PhoneBook	pb;	std::string input;	std::cout << std::endl << "The program only accepts ADD, SEARCH and EXIT." << std::endl;	while (true) {		std::cout << "Enter one of those (ADD, SEARCH, EXIT): ";		std::cin >> input;		if (std::cin.eof())			exit(0);		if (input == "ADD")			pb.add();		else if (input == "SEARCH")			pb.show();		else if (input == "EXIT")			exit(0);	}	return (0);}